{
  "statements": [
    {
      "statement": "USING PERIODIC COMMIT LOAD CSV WITH HEADERS FROM 'file:///Users/jonathanbowker/Dropbox/CryDEX-DEMO/OutPut/ShadowHashIndex/ShadowBlocks.csv' AS row MERGE (a:Shadow {shadowFileUID:row.PartitionKey, shadowHash:row.RowKey, shadowTimestamp:row.TimeStamp,  shadowNLP:row.NLPMatch, shadowNLPPosition:row.NLPMatchPosition, shadowWB:row.WBMatch, shadowWBPosition:row.WBMatchPosition,  shadowPT:row.PTMatch, shadowPTPosition:row.PTMatchPosition, shadowRB:row.RBMatch, shadowRBPosition:row.RBMatchPosition, shadowContent:row.IndexText})"
    }
  ]
