{
  "statements": [
    {
      "statement": "MATCH (n:Asset) WHERE EXISTS(n.assetFileUID) WITH DISTINCT n.assetFileUID AS assetFileUID, n.assetTimestamp AS assetTimestamp CREATE (a:AssetFile {assetFileUID:assetFileUID, assetTimestamp:assetTimestamp})"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:Asset)  WHERE n.assetNLP='1' CREATE (a:AssetNLP{assetFileUID:n.assetFileUID, assetHash:n.assetHash, assetNLPPosition:n.assetNLPPosition, assetContent:n.assetContent})"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:Asset)  WHERE n.assetPT='1' CREATE (a:AssetPT{assetFileUID:n.assetFileUID, assetHash:n.assetHash, assetPTPosition:n.assetPTPosition, assetContent:n.assetContent})"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:Asset)  WHERE n.assetRB='1' CREATE (a:AssetRB{assetFileUID:n.assetFileUID, assetHash:n.assetHash, assetRBPosition:n.assetRBPosition, assetContent:n.assetContent})"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:Asset)  WHERE n.assetWB='1' CREATE (a:AssetWB{assetFileUID:n.assetFileUID, assetHash:n.assetHash, assetWBPosition:n.assetWBPosition, assetContent:n.assetContent})"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:Shadow) WHERE EXISTS(n.shadowFileUID) WITH DISTINCT n.shadowFileUID AS shadowFileUID, n.shadowTimestamp AS shadowTimestamp CREATE (a:ShadowFile {shadowFileUID:shadowFileUID, shadowTimestamp:shadowTimestamp})"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:Shadow)  WHERE n.shadowNLP='1' CREATE (a:ShadowNLP{shadowFileUID:n.shadowFileUID, shadowHash:n.shadowHash, shadowNLPPosition:n.shadowNLPPosition, shadowContent:n.shadowContent})"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:Shadow)  WHERE n.shadowPT='1' CREATE (a:ShadowPT{shadowFileUID:n.shadowFileUID, shadowHash:n.shadowHash, shadowPTPosition:n.shadowPTPosition, shadowContent:n.shadowContent})"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:Shadow)  WHERE n.shadowRB='1' CREATE (a:ShadowRB{shadowFileUID:n.shadowFileUID, shadowHash:n.shadowHash, shadowRBPosition:n.shadowRBPosition, shadowContent:n.shadowContent})"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:Shadow)  WHERE n.shadowWB='1' CREATE (a:ShadowWB{shadowFileUID:n.shadowFileUID, shadowHash:n.shadowHash, shadowWBPosition:n.shadowWBPosition, shadowContent:n.shadowContent})"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:AssetFile),(m:AssetNLP) WHERE n.assetFileUID = m.assetFileUID WITH n.assetFileUID AS assetFileUID, m.assetFileUID AS assetNLPFileUID,  COUNT(m.assetHash) AS matchNLPCount CREATE (n)-[r:ASSET_CHILD_NLP_COUNT { assetNLPCount:  matchNLPCount  }]->(m) RETURN assetFileUID, matchNLPCount"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:AssetFile),(m:AssetPT) WHERE n.assetFileUID = m.assetFileUID WITH n.assetFileUID AS assetFileUID, m.assetFileUID AS assetPTFileUID,  COUNT(m.assetHash) AS matchPTCount CREATE (n)-[r:ASSET_CHILD_PT_COUNT {assetPTCount:  matchPTCount  }]->(m) RETURN assetFileUID, matchPTCount"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:AssetFile),(m:AssetRB) WHERE n.assetFileUID = m.assetFileUID WITH n.assetFileUID AS assetFileUID, m.assetFileUID AS assetRBFileUID,  COUNT(m.assetHash) AS matchRBCount CREATE (n)-[r:ASSET_CHILD_RB_COUNT { assetRBCount:  matchRBCount  }]->(m) RETURN assetFileUID, matchRBCount"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:AssetFile),(m:AssetWB) WHERE n.assetFileUID = m.assetFileUID WITH n.assetFileUID AS assetFileUID, m.assetFileUID AS assetWBFileUID,  COUNT(m.assetHash) AS matchWBCount CREATE (n)-[r:ASSET_CHILD_WB_COUNT { assetWBCount:  matchWBCount  }]->(m) RETURN assetFileUID, matchWBCount"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:ShadowFile),(m:ShadowNLP) WHERE n.shadowFileUID = m.shadowFileUID WITH n.shadowFileUID AS shadowFileUID, m.shadowFileUID AS shadowNLPFileUID,  COUNT(m.shadowHash) AS matchNLPCount CREATE (n)-[r:SHADOW_CHILD_NLP_COUNT { shadowNLPCount:  matchNLPCount  }]->(m) RETURN shadowFileUID, matchNLPCount"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:ShadowFile),(m:ShadowPT) WHERE n.shadowFileUID = m.shadowFileUID WITH n.shadowFileUID AS shadowFileUID, m.shadowFileUID AS shadowNLPFileUID,  COUNT(m.shadowHash) AS matchPTCount CREATE (n)-[r:SHADOW_CHILD_PT_COUNT { shadowPTCount:  matchPTCount  }]->(m) RETURN shadowFileUID, matchPTCount"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:ShadowFile),(m:ShadowRB) WHERE n.shadowFileUID = m.shadowFileUID WITH n.shadowFileUID AS shadowFileUID, m.shadowFileUID AS shadowNLPFileUID,  COUNT(m.shadowHash) AS matchRBCount CREATE (n)-[r:SHADOW_CHILD_RB_COUNT { shadowRBCount:  matchRBCount  }]->(m) RETURN shadowFileUID, matchRBCount"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:ShadowFile),(m:ShadowWB) WHERE n.shadowFileUID = m.shadowFileUID WITH n.shadowFileUID AS shadowFileUID, m.shadowFileUID AS shadowNLPFileUID,  COUNT(m.shadowHash) AS matchWBCount CREATE (n)-[r:SHADOW_CHILD_WB_COUNT { shadowWBCount:  matchWBCount  }]->(m) RETURN shadowFileUID, matchWBCount"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:AssetRB) WITH n.assetFileUID AS assetFileUID, COUNT(n.assetHash) AS assetHashCount RETURN DISTINCT assetFileUID, assetHashCount"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (m:ShadowRB) WITH m.shadowFileUID AS shadowFileUID, COUNT(m.shadowHash) AS shadowHashCount RETURN DISTINCT shadowFileUID, shadowHashCount"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (m:AssetFile),(p:AssetNLP) WHERE m.assetFileUID = p.assetFileUID MERGE (m)-[:ASSET_CHILD_NLP]->(p)"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (m:AssetFile),(p:AssetPT) WHERE m.assetFileUID = p.assetFileUID MERGE (m)-[:ASSET_CHILD_PT]->(p)"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (m:AssetFile),(p:AssetRB) WHERE m.assetFileUID = p.assetFileUID MERGE (m)-[:ASSET_CHILD_RB]->(p)"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (m:AssetFile),(p:AssetWB) WHERE m.assetFileUID = p.assetFileUID MERGE (m)-[:ASSET_CHILD_WB]->(p)"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (m:ShadowFile),(p:ShadowNLP) WHERE m.shadowFileUID = p.shadowFileUID MERGE (m)-[:SHADOW_CHILD_NLP]->(p)"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (m:ShadowFile),(p:ShadowPT) WHERE m.shadowFileUID = p.shadowFileUID MERGE (m)-[:SHADOW_CHILD_PT]->(p)"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (m:ShadowFile),(p:ShadowRB) WHERE m.shadowFileUID = p.shadowFileUID MERGE (m)-[:SHADOW_CHILD_RB]->(p)"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (m:ShadowFile),(p:ShadowWB) WHERE m.shadowFileUID = p.shadowFileUID MERGE (m)-[:SHADOW_CHILD_WB]->(p)"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:AssetRB) WITH n.assetFileUID AS assetFileUID, COUNT(n.assetHash) AS assetHashCount RETURN DISTINCT assetFileUID, assetHashCount"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (m:ShadowRB) WITH m.shadowFileUID AS shadowFileUID, COUNT(m.shadowHash) AS shadowHashCount RETURN DISTINCT shadowFileUID, shadowHashCount"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:AssetRollingBlock),(m:ShadowRollingBlock) WHERE n.contentHash = m.contentHash WITH n.fileUID AS assetRollingBlockFileUID, m.fileUID AS shadowRollingBlockFileUID,  COUNT(m.fileUID) AS count RETURN  assetRollingBlockFileUID, shadowRollingBlockFileUID, count ORDER BY count desc"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:AssetPT),(m:ShadowPT) WHERE n.assetHash = m.shadowHash WITH n.assetFileUID AS assetFileUID, m.shadowFileUID AS shadowFileUID,  COUNT(*) AS matchingPTCount RETURN  assetFileUID, matchingPTCount, shadowFileUID"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:AssetRB),(m:ShadowRB) WHERE n.assetHash = m.shadowHash WITH n.assetFileUID AS assetFileUID, m.shadowFileUID AS shadowFileUID,  COUNT(*) AS matchingRBCount RETURN  assetFileUID, matchingRBCount, shadowFileUID"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:AssetWB),(m:ShadowWB) WHERE n.assetHash = m.shadowHash WITH n.assetFileUID AS assetFileUID, m.shadowFileUID AS shadowFileUID,  COUNT(*) AS matchingWBCount RETURN  assetFileUID, matchingWBCount, shadowFileUID"
    }
  ]
}

{
  "statements": [
    {
      "statement": "MATCH (n:AssetHash) WITH n.hashUID AS assetFileUID, COUNT(n:hashUID) AS assetHashCount MATCH (p:AssetHash),(q:ShadowHash) WHERE p.hash=q.hash WITH assetFileUID, assetHashCount, p.hashUID AS shadowFileUID, COUNT(p:hashUID) AS matchCount MATCH (assetFileUID),(shadowFileUID) WHERE assetFileUID=shadowFileUID RETURN assetFileUID, assetHashCount, shadowFileUID, matchCount, (matchCount*1.0^3/assetHashCount*1.0^3)*1^3 AS diffScore ORDER BY diffScore"
    }
  ]
}
